create database VnSport
go

use VnSport
go
 

--Tạo bảng tài khoản
create table TaiKhoan
(tenDangNhap varchar(50) primary key,
matKhau varchar(30) not null,
loai nvarchar(20) not null
)

select * from TaiKhoan

