--Tạo bảng thông tin khách hàng
create table ThongTinKhachHang
(maKhachHang int primary key IDENTITY (1,1),
tenDangNhap varchar(50) foreign key references TaiKhoan(tenDangNhap) on update cascade on delete cascade,
hoTen nvarchar(40),
sdt varchar(10) not null,
tinh nvarchar(50) not null,
huyen nvarchar(50) not null,
xa nvarchar(50) not null,
diaChi nvarchar(50) not null,
ghiChu nvarchar(100),
hinhAnh nvarchar(100)
)


--Nhập dữ liệu bảng Thông tin khách hàng


