--Tạo bảng sản phẩm 
create table SanPham
(maSanPham nvarchar(50) primary key,
tenSanPham nvarchar(100),
mau nvarchar(50),
kichThuoc nvarchar(200),
chatLieu nvarchar(100),
thuongHieu nvarchar(50),
gioiThieu nvarchar(max),
moTa nvarchar(max),
giaCu int,
giaMoi int,
anh1 nvarchar(100),
anh2 nvarchar(100),
anh3 nvarchar(100),
maLoai int,
constraint Fk_Sp2 foreign key (maLoai) references LoaiSanPham(maLoai) on update cascade on delete cascade
)


-- Nhập thông tin bảng sản phẩm 

INSERT [dbo].[SanPham] ([maSanPham], [tenSanPham], [mau], [kichThuoc], [chatLieu], [thuongHieu], [gioiThieu], [moTa], [giaCu], [giaMoi], [anh1], [anh2], [anh3], [maLoai]) VALUES (N' GFHY004-01', N'Đai Lưng Tập Thể Hình Da Bò Cao Cấp Glofit GFHY004-01', NULL, N'L/M/S', NULL, NULL, NULL, N'<h4 style="box-sizing: border-box; font-family: &quot;Baloo Thambi 2&quot;, cursive; font-weight: inherit; line-height: 30px; color: rgb(68, 68, 68); margin: 0px 0px 5px; font-size: 20px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; vertical-align: baseline;">
	Đai lưng sử dụng chất liệu da cao cấp, rất &ecirc;m v&agrave; thỏa m&aacute;i hỗ trợ v&ugrave;ng eo cực tốt cho bạn tập luyện ở cường độ cao kh&ocirc;ng sợ bị chấn thương v&ugrave;ng thắt lưng<br style="box-sizing: border-box;" />
	Đai lưng cao cấp Glofit c&oacute; thiết kế sang trọng v&agrave; tinh tế từng chi tiết nhỏ v&agrave; đường chỉ may viền, gi&uacute;p sản phẩm c&oacute; độ bền cao.<br style="box-sizing: border-box;" />
	Sản phẩm ph&ugrave; hợp để tập luyện thể h&igrave;nh hoặc cho những ai thường phải khu&acirc;n v&aacute;c nặng<br style="box-sizing: border-box;" />
	Đặc biệt: Với những ai bị đau nhức v&ugrave;ng thắt lưng cũng c&oacute; thể sử dụng, sản phẩm rất &ecirc;m v&agrave; thỏa m&aacute;i<br style="box-sizing: border-box;" />
	C&aacute;ch chọn size ph&ugrave; hợp:<br style="box-sizing: border-box;" />
	Size: S ( V&ograve;ng eo khoảng 70cm &ndash; 86cm )<br style="box-sizing: border-box;" />
	Size: M ( V&ograve;ng eo khoảng 86cm &ndash; 100cm )<br style="box-sizing: border-box;" />
	Size: L ( V&ograve;ng eo khoảng 100cm &ndash; 110cm )<br style="box-sizing: border-box;" />
	Lưu &yacute;: C&oacute; thể t&ugrave;y chỉnh độ siết của đai cho ph&ugrave; hợp từng số đo v&ograve;ng eo</h4>
<p>
	<img src="https://vnsport.com.vn/wp-content/uploads/2020/08/dai-lung-tap-gym-da-bo-cao-cap-GFHY004-1.jpg" /></p>
', 1890000, 1550000, N'GFHY004-01.png', NULL, NULL, 5)
