INSERT [dbo].[SanPham] ([maSanPham], [tenSanPham], [mau], [kichThuoc], [chatLieu], [thuongHieu], [gioiThieu], [moTa], [giaCu], [giaMoi], [anh1], [anh2], [anh3], [maLoai]) VALUES (N'MK1318', N'Tất Yoga Chống Trượt Xỏ Ngón Cao Cấp MEIKAN', N'Xanh / Vàng / Hồng / Xám / Đen', N'Size Chân Từ 35-39', NULL, NULL, N'Tất tập yoga chống trượt có thiết kế xỏ ngón giúp bạn thỏa mái hơn khi sử dụng và luôn có cảm giác tự nhiên. Sản phẩm được dệt kim bằng sợi cotton nên có độ thoáng khí cao và thấm hút mồ hôi tốt. Vớ chống trượt dùng chuyên dụng cho tập yoga, tuy nhiên bạn cũng có thể sử dụng cho các bộ môn thể thao khác.', NULL, 95000, 75000, N'MK1318.png', NULL, NULL, 9)
