-- Tạo bảng hóa đơn
create table HoaDon
(maHoaDon int primary key IDENTITY (1,1) not null,
maKhachHang int,
ngayBan datetime,
tongSoLuong int,
giamGia int,
thanhTien int,
hinhThucThanhToan nvarchar(100)
constraint Fk_HD3 foreign key (maKhachHang) references ThongTinKhachHang(maKhachHang)
)


