create table ChiTietHoaDon
(maChiTietHoaDon int primary key identity(1, 1) not null,
maSanPham nvarchar(50),
soLuong int,
donGia int,
maHoaDon int,
constraint Fk_CTHD1 foreign key (maSanPham) references SanPham(MaSanPham),
constraint Fk_CTHD2 foreign key (maHoaDon) references HoaDon(maHoaDon)
)



--taoj bảng tin tức
create table TinTuc
(maTinTuc int primary key,
tieuDe nvarchar(max) not null,
noiDung nvarchar(max),
anh1 nvarchar(50),
anh2 nvarchar(50),
anh3 nvarchar(50),
ngayDang datetime,
)













